def countFloors(instructions)
  return instructions.count("(") - instructions.count(")");
end

def findPositionAt(instructions, position)
  i = 0
  floor = 0
  while floor > position do
    if instructions[i] === "("
      floor += 1
    elsif instructions[i] === ")"
      floor += -1
    end
    i += 1
  end
  return i
end

